import React, { useState } from 'react';

const Room = () => {
  const [showPopup, setShowPopup] = useState(false);

  const handleAddClick = () => {
    setShowPopup(true);
  };

  const handleAddRoom = () => {
    // This will be logic to add room which we do not have yet
    setShowPopup(false); // Close the popup after clicking add
  };

  const handleCreateRoom = () => {
    // This will be logic to create a room which we do not have yet
    setShowPopup(false); // Close the popup after clicking create
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <div className="container flex flex-col items-start justify-start h-screen px-9 py-12 relative">
      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-10 px-16 rounded flex items-center"
        onClick={handleAddClick}
      >
        <span className="mr-2 text-4xl">+</span>
        Add Room
      </button>

      {showPopup && (
        <div className="fixed inset-0 flex items-center justify-center">
          <div className="bg-white p-8 rounded-lg shadow-md relative w-80 flex flex-col items-center">
            <button
              className="text-red-600 hover:text-red-800 absolute top-2 right-2"
              onClick={handleClosePopup}
            >
              <span className="text-2xl font-bold">&times;</span>
            </button>

            <button
              className="bg-blue-500 hover:bg-blue-700 text-white px-4 py-2 rounded mb-2"
              onClick={handleAddRoom}
            >
              Add
            </button>

            <p className="text-lg font-semibold my-2">OR</p>

            <button
              className="bg-green-500 hover:bg-green-700 text-white px-4 py-2 rounded mt-2"
              onClick={handleCreateRoom}
            >
              Create
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Room;
