import React, { useState } from 'react';

const Room = () => {
  const [showPopup, setShowPopup] = useState(false);

  const handleAddClick = () => {
    setShowPopup(true);
  };

  const handleAddRoom = () => {
    // Implement the logic for adding a room
    // This function will be called when the "Add" button is clicked
    setShowPopup(false); // Close the popup after adding a room
  };

  const handleCreateRoom = () => {
    // Implement the logic for creating a room
    // This function will be called when the "Create" button is clicked
    setShowPopup(false); // Close the popup after creating a room
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <div className="container flex flex-col items-start justify-start h-screen px-9 py-12">
      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-10 px-16 rounded flex items-center"
        onClick={handleAddClick}
      >
        <span className="mr-2 text-4xl">+</span>
        Add Room
      </button>

      {showPopup && (
        <div className="fixed inset-0 flex items-center justify-center">
          <div className="bg-white p-8 rounded-lg shadow-md">
            <div className="flex justify-end">
              <button
                className="text-red-600 hover:text-red-800"
                onClick={handleClosePopup}
              >
                <span className="text-2xl font-bold">&times;</span>
              </button>
            </div>
            <p className="text-lg font-semibold mb-4">Choose an action:</p>
            <div className="flex gap-4">
              <button
                className="bg-blue-500 hover:bg-blue-700 text-white px-4 py-2 rounded"
                onClick={handleAddRoom}
              >
                Add
              </button>
              <button
                className="bg-green-500 hover:bg-green-700 text-white px-4 py-2 rounded"
                onClick={handleCreateRoom}
              >
                Create
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Room;
