// App.js
import React, { useEffect, useState } from 'react';
import { Route, Routes, useNavigate, useLocation } from 'react-router-dom';
import UserPageHeader from './UserPageHeader';
import Home from './util/Home';
import Login from './util/Login';
import Register from './util/Register';
import Room from './util/Room';
import UserPage from './util/UserPage';

export default function App() {
  const navigate = useNavigate();
  const location = useLocation();
  const routesWithoutHeader = ['login', 'register']; // remove leading '/'
  
  const [shouldDisplayHeader, setShouldDisplayHeader] = useState(false);

  useEffect(() => {
    const currentRoute = location.pathname.split('/')[1];
    setShouldDisplayHeader(!routesWithoutHeader.includes(currentRoute));
  }, [location.pathname, routesWithoutHeader]);

  // Function to handle navigation to Room.js
  const navigateToRoom = () => {
    navigate('/room');
  };

  return (
    <React.Fragment>
      {/* Conditionally render UserPageHeader */}
      {shouldDisplayHeader && <UserPageHeader navigateToRoom={navigateToRoom} />}
      
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/room" element={<Room shouldDisplayHeader={false} />} />
        <Route path="/WelcomeUser/:userId" element={<UserPage shouldDisplayHeader={false} />} />
      </Routes>
    </React.Fragment>
  );
}
